class PasswordsController < ApplicationController\n  def new\n    # Display the form for entering the email address\n  end\n\n  def create\n    # Find the user by email and send a password reset email with a token\n    user = User.find_by(email: params[:email])\n    if user\n      # Generate a token and send an email\n      PasswordResetMailer.send_reset_email(user).deliver_now\n      redirect_to root_path, notice: "Email sent with password reset instructions."\n    else\n      render :new\n    end\n  end\n\n  def edit\n    # Display the form for resetting the password\n    @token = params[:id]\n  end\n\n  def update\n    # Update the user's password based on the token\n    user = User.find_by(password_reset_token: params[:id])\n    if user && user.update(password: params[:password])\n      redirect_to root_path, notice: "Password updated successfully."\n    else\n      render :edit\n    end\n  end\nend