class Employee < ActiveResource::Base\n  self.site = "http://example.com"\n\n  def self.find(id, options = {})\n    if options[:params] && options[:params][:company_id]\n      prefix_options = { company_id: options[:params][:company_id] }\n      find_one(id, prefix_options)\n    else\n      super(id, options)\n    end\n  end\n\n  def self.find_one(id, prefix_options)\n    # Construct the URL manually to include the company_id\n    url = "#{self.site}/companies/#{prefix_options[:company_id]}/employees/#{id}.#{self.format.extension}"\n    connection.get(url, headers)\n  end\nend