require 'nokogiri'\n\nhtml = '<html><body><div id="menu">Hello!</div></body></html>'\ndoc = Nokogiri::HTML(html)\n\n# Select an element by its ID\nmenu = doc.css('#menu')\n\n# Get the inner HTML of the element\nputs menu.inner_html # Output: Hello!\n\n# Prepend some content to the element\nmenu.prepend 'New content! '\nputs menu.inner_html # Output: New content! Hello!\n\n# Add some content after the element\nmenu.after '<p>This is a new paragraph!</p>'\nputs doc.to_html # Output: <html><body><div id="menu">New content! Hello!</div><p>This is a new paragraph!</p></body></html>