require 'gtk2'\n\n# Create a new Glade project\nproject = Glade::Project.new('example.glade')\n\n# Get the main window\nwindow = project.get_widget('main_window')\n\n# Connect a signal to a button\nbutton = project.get_widget('button1')\nbutton.signal_connect('clicked') do\n  window.title = 'Button clicked!'\nend\n\n# Show the window\nwindow.show_all\n\n# Run the GTK+ main loop\nGtk.mai