# app/models/upload.rb\nclass Upload < ApplicationRecord\n  has_one_attached :file\n\n  def generate_thumbnail\n    require 'image_processing'\n    require 'pdf-reader'\n\n    pdf = PDF::Reader.new(file.blob)\n    page = pdf.pages.first\n    image = page.render(width: 200, height: 200) # Adjust the dimensions as needed\n\n    # Save the thumbnail as an attachment\n    thumbnail = image_processing.resize_to_fit(200, 200)\n    thumbnail_attach(thumbnail, filename: "#{file.filename}_thumbnail.jpg")\n  end\nend