def set_concept_status\n  status = case [params[:concept][:consulted_legal], params[:concept][:consulted_marketing]]\n           when [0, 1] then 'Awaiting Compliance Approval'\n           when [1, 0] then 'Awaiting Marketing Approval'\n           when [0, 0] then 'Awaiting Marketing & Legal Approval'\n           else 'Pending Approval'\n           end\n  @concept.attributes = { status: status }\nend