# config/deploy.rb\nset :application, 'my_app'\nset :repo_url, 'git@github.com:my_app/my_app.git'\nset :deploy_to, '/var/www/my_app'\n\n# Define roles (e.g., web, db)\nrole :web, %w{server1 server2}\nrole :db, %w{server3}\n\n# Define tasks\nnamespace :deploy do\n  desc 'Run database migrations'\n  task :migrate do\n    on roles(:db) do\n      execute "php app/console doctrine:migrations:migrate --no-interaction"\n    end\n  end\nend