require 'active_record'\n\n# Establish a connection to the database\nActiveRecord::Base.establish_connection(\n  adapter: 'mysql2',\n  host: 'your_host',\n  database: 'your_database',\n  username: 'your_username',\n  password: 'your_password'\n)\n\n# Define the source and destination tables\nclass SourceTable < ActiveRecord::Base\n  self.table_name = 'tablename'\nend\n\nclass DestTable < ActiveRecord::Base\n  self.table_name = 'dest_table'\nend\n\n# Copy the data\nSourceTable.find_each do |record|\n  DestTable.create!(\n    field1: record.myfield1,\n    field2: record.myfield2,\n    field3: record.id,\n    blobfield: record.blobfield,\n    field4: 'SomeConstant'\n  )\nend