# In your Miniature model\nscope :with_ownership, ->(user) { includes(:ownerships).where("ownerships.user_id = ? OR ownerships.user_id IS NULL", user.id) }\n\n# In your controller\n@miniatures = Miniature.with_ownership(current_user)\n\n# In your view\n<% for miniature in @miniatures %>\n  <td><%= link_to miniature.name, miniature %></td>\n  <td><%= text_field_tag "counts[#{miniature.id}]", miniature.ownerships.find_by_user_id(current_user.id).try(:have_count) || 0, :size => 2 %></td>\n<% end %>