require 'socket'\n\n# Create a TCP socket\nsock = TCPSocket.new('irc.example.com', 6667)\n\n# Send a NICK command to set your nickname\nsock.puts "NICK mynick"\n\n# Send a USER command to set your username\nsock.puts "USER myuser 0 * :My Real Name"\n\n# Join a channel\nsock.puts "JOIN #mychannel"\n\n# Send a message to the channel\nsock.puts "PRIVMSG #mychannel :Hello, world!"\n\n# Close the socket\nsock.close