# features/step_definitions/twitter_oauth_steps.rb\nThen(/^I have granted Twitter OAuth access$/) do\n  client = mock_twitter_oauth\n  # Simulate the authorization flow\n  visit "/oauth/authorize"\n  fill_in "username", with: "username"\n  fill_in "password", with: "password"\n  click_button "Authorize"\nend\n\nThen(/^I have not granted Twitter OAuth access$/) do\n  unmock_twitter_oauth\n  # Simulate the authorization flow without granting access\n  visit "/oauth/authorize"\n  click_button "Cancel"\nend