class PdfGeneratorController < ApplicationController\n  def create\n    # Get the necessary parameters from the request\n    text = params[:text]\n    layout = params[:layout]\n\n    # Create the TeX document and generate the PDF\n    doc = RTex::Document.new\n    doc.text text\n    pdf = RTex::PdfGenerator.new(doc)\n    pdf.generate("output.pdf")\n\n    # Send the PDF back to the client\n    send_file "output.pdf", type: "application/pdf"\n  end\nend