def insert_spaces(text)\n  words = text.scan(/([a-zA-Z]+)(-?\d{1,4}\.\d)?/)\n  result = ""\n  words.each do |word, space|\n    result += word\n    if space && space.to_f < -300\n      result += " "\n    end\n  end\n  result\nend\n\n# Example usage:\ntext = "The-571.3neural-573.7system-577.4underly13.9ing-577.2face-573.0perc13.7eption-574.9must-572.1repr20.8esent-577.0theunchangin14.4g-538.5featur16.5es-529.5of-536.6a-531.4face"\nputs insert_spaces(text)