class Recipe\n  def pour(&block)\n    @ingredients = []\n    block.call\n    @ingredients\n  end\n\n  def amount(amount)\n    @amount = amount\n  end\n\n  def of(ingredient)\n    @ingredients << { ingredient: ingredient, amount: @amount }\n  end\nend\n\ndef description(name, &block)\n  recipe = Recipe.new\n  block.call(recipe)\n  # Send commands to Arduino using recipe.ingredients\nend