USE Mydatabase\nGO\n\n-- Create the Test login\nCREATE LOGIN Test WITH PASSWORD = 'Password123'\nGO\n\n-- Create the Test database user\nCREATE USER Test FOR LOGIN Test\nGO\n\n-- Grant select permission on the view to the Public role\nGRANT SELECT ON MyView TO PUBLIC\nGO\n\n-- Check the effective permissions of the Test user\nEXECUTE AS USER = 'Test'\nGO\n\nSELECT * FROM fn_my_permissions('MyView', 'OBJECT')\nGO\n\nREVERT\nGO