CREATE TABLE Orders (\n    OrderID INT PRIMARY KEY,\n    CustomerID INT,\n    OrderDate DATETIME,\n    Total DECIMAL(10, 2)\n);\n\nINSERT INTO Orders (OrderID, CustomerID, OrderDate, Total)\nSELECT TOP 10000 \n    ROW_NUMBER() OVER (ORDER BY t1.object_id),\n    ABS(CHECKSUM(NEWID())) % 100,\n    DATEADD(DAY, ABS(CHECKSUM(NEWID())) % 1000, '2010-01-01'),\n    CAST(RAND() * 100 AS DECIMAL(10, 2))\nFROM master.sys.all_columns t1\nCROSS JOIN master.sys.all_columns t2;