SELECT \n    AVG(CASE WHEN GenSetName LIKE 'GEA3519' THEN KW END) AS GEN_101_AVG,\n    AVG(CASE WHEN GenSetName LIKE 'GEA3520' THEN KW END) AS GEN_201_AVG,\n    AVG(CASE WHEN GenSetName LIKE 'GEA3521' THEN KW END) AS GEN_301_AVG,\n    AVG(CASE WHEN GenSetName LIKE 'GEA3522' THEN KW END) AS GEN_401_AVG\nFROM genset\nWHERE GenDate >= '2003-01-01 00:00:00' AND GenDate < '2003-02-01 00:00:00'