SELECT \n    (SELECT AVG(KW) FROM genset WHERE GenSetName LIKE 'GEA3519' AND GenDate >= '2003-01-01 00:00:00' AND GenDate < '2003-02-01 00:00:00') AS GEN_101_AVG,\n    (SELECT AVG(KW) FROM genset WHERE GenSetName LIKE 'GEA3520' AND GenDate >= '2003-01-01 00:00:00' AND GenDate < '2003-02-01 00:00:00') AS GEN_201_AVG,\n    (SELECT AVG(KW) FROM genset WHERE GenSetName LIKE 'GEA3521' AND GenDate >= '2003-01-01 00:00:00' AND GenDate < '2003-02-01 00:00:00') AS GEN_301_AVG,\n    (SELECT AVG(KW) FROM genset WHERE GenSetName LIKE 'GEA3522' AND GenDate >= '2003-01-01 00:00:00' AND GenDate < '2003-02-01 00:00:00') AS GEN_401_AVG