-- Define the backup files and database names\nDECLARE @WeeklyFullBackupFile nvarchar(255) = 'C:\Backup\WeeklyFullBackup.bak'\nDECLARE @DailyIncrementalBackupFile nvarchar(255) = 'C:\Backup\DailyIncrementalBackup.bak'\nDECLARE @TransactionLogBackupFile nvarchar(255) = 'C:\Backup\TransactionLogBackup.trn'\nDECLARE @ReportingDatabaseName nvarchar(255) = 'ReportingDB'\n\n-- Restore the weekly full backup with NORECOVERY\nRESTORE DATABASE @ReportingDatabaseName\nFROM DISK = @WeeklyFullBackupFile\nWITH NORECOVERY, REPLACE\n\n-- Restore the daily incremental backup with NORECOVERY\nRESTORE DATABASE @ReportingDatabaseName\nFROM DISK = @DailyIncrementalBackupFile\nWITH NORECOVERY\n\n-- Restore the transaction log backup with RECOVERY\nRESTORE LOG @ReportingDatabaseName\nFROM DISK = @TransactionLogBackupFile\nWITH RECOVERY