CREATE TABLE users (\n  id INT PRIMARY KEY,\n  name VARCHAR(255),\n  email VARCHAR(255),\n  created_timestamp TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  updated_timestamp TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP\n);\n\nCREATE TABLE customers (\n  id INT PRIMARY KEY,\n  name VARCHAR(255),\n  address VARCHAR(255),\n  created_timestamp TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  updated_timestamp TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP\n);