CREATE OR REPLACE PROCEDURE get_data_field(p_id IN VARCHAR2)\nIS\n  TYPE t_cursor IS REF CURSOR;\n  v_cursor t_cursor;\n  v_info_field VARCHAR2(100);\n  v_data_field VARCHAR2(100);\n  v_sql VARCHAR2(4000);\nBEGIN\n  FOR i IN (SELECT table_name FROM all_tables WHERE table_name LIKE 'TABLE_%') LOOP\n    v_sql := 'SELECT info_field, data_field FROM ' || i.table_name || ' WHERE some_id = :1';\n    OPEN v_cursor FOR v_sql USING p_id;\n    LOOP\n      FETCH v_cursor INTO v_info_field, v_data_field;\n      EXIT WHEN v_cursor%NOTFOUND;\n      DBMS_OUTPUT.PUT_LINE(v_info_field || ', ' || v_data_field);\n    END LOOP;\n    CLOSE v_cursor;\n  END LOOP;\nEND;\n/