-- Collect performance metrics\nSELECT \n    cpu_usage = (SELECT value FROM sys.dm_os_performance_counters WHERE counter_name = 'CPU usage %'),\n    mem_usage = (SELECT value FROM sys.dm_os_performance_counters WHERE counter_name = 'Memory usage %'),\n    disk_queue = (SELECT value FROM sys.dm_os_performance_counters WHERE counter_name = 'Disk queue length'),\n    disk_latency = (SELECT value FROM sys.dm_os_performance_counters WHERE counter_name = 'Avg. disk sec/transfer');