CREATE VIEW CombinedQuery AS\nSELECT * FROM MappingQuery1\nUNION\nSELECT * FROM MappingQuery2;\n\nCREATE TRIGGER trg_CombinedQuery_Update\nON CombinedQuery\nINSTEAD OF UPDATE\nAS\nBEGIN\n    -- Handle updates here, e.g.\n    UPDATE MappingQuery1\n    SET column1 = i.column1, column2 = i.column2\n    FROM inserted i\n    WHERE MappingQuery1.id = i.id;\n\n    UPDATE MappingQuery2\n    SET column1 = i.column1, column2 = i.column2\n    FROM inserted i\n    WHERE MappingQuery2.id = i.id;\nEND;