SELECT \n       s.session_id,\n       r.status,\n       r.command,\n       r.cpu_time,\n       r.total_elapsed_time,\n       t.text AS query_text\n   FROM \n       sys.dm_exec_requests r\n   INNER JOIN \n       sys.dm_exec_sessions s ON r.session_id = s.session_id\n   CROSS APPLY \n       sys.dm_exec_sql_text(r.sql_handle) t\n   WHERE \n       s.program_name = 'MS SQL Management Studio'