CREATE TABLE #Temp (VisitingCount int, [Time] int)\n\nDECLARE @DateNow DATETIME, @i int, @Time int\nSET @DateNow = '00:00'\nSET @i = 1\nWHILE (@i < 48)\nBEGIN\n    SET @DateNow = DATEADD(minute, 30, @DateNow)\n    SET @Time = (DATEPART(hour, @DateNow) * 60 + DATEPART(minute, @DateNow)) / 30\n    INSERT INTO #Temp(VisitingCount, [Time]) VALUES (0, @Time)\n    SET @i = @i + 1\nEND\n\nSELECT \n    COALESCE(SUM(s.VisitingCount), t.VisitingCount) AS VisitingCount, \n    t.[Time]\nFROM \n    #Temp t\n    LEFT JOIN (\n        SELECT \n            COUNT(page) AS VisitingCount, \n            (DATEPART(hour, Date) * 60 + DATEPART(minute, Date)) / 30 AS [Time]\n        FROM \n            scr_SecuristLog\n        WHERE \n            Date BETWEEN '2009-05-04 10:30' AND '2009-05-04 12:30'\n        GROUP BY \n            (DATEPART(hour, Date) * 60 + DATEPART(minute, Date)) / 30\n    ) s ON t.[Time] = s.[Time]\nGROUP BY \n    t.[Time]