CREATE TABLE Events (\n    EventID INT PRIMARY KEY,\n    EventType VARCHAR(255) NOT NULL,\n    Description VARCHAR(255)\n);\n\nCREATE TABLE Schedules (\n    ScheduleID INT PRIMARY KEY,\n    EventID INT NOT NULL,\n    FOREIGN KEY (EventID) REFERENCES Events(EventID)\n);\n\nCREATE TABLE WeeklySchedules (\n    ScheduleID INT PRIMARY KEY,\n    DayOfWeek INT NOT NULL, -- 1 = Sunday, 2 = Monday, ..., 7 = Saturday\n    Time TIME NOT NULL,\n    FOREIGN KEY (ScheduleID) REFERENCES Schedules(ScheduleID)\n);\n\nCREATE TABLE MonthlySchedules (\n    ScheduleID INT PRIMARY KEY,\n    DayOfMonth INT NOT NULL, -- 1-31\n    Time TIME NOT NULL,\n    FOREIGN KEY (ScheduleID) REFERENCES Schedules(ScheduleID)\n);\n\nCREATE TABLE AnnualSchedules (\n    ScheduleID INT PRIMARY KEY,\n    MonthOfYear INT NOT NULL, -- 1 = January, 2 = February, ..., 12 = December\n    DayOfMonth INT NOT NULL,\n    Time TIME NOT NULL,\n    FOREIGN KEY (ScheduleID) REFERENCES Schedules(ScheduleID)\n);