WITH Weeks AS (\n    SELECT \n        DATEADD(dw, -DATEPART(dw, '2007-05-29'), '2007-05-29') AS StartDate,\n        DATEADD(dw, 6, DATEADD(dw, -DATEPART(dw, '2007-05-29'), '2007-05-29')) AS EndDate,\n        1 AS WeekNum\n    UNION ALL\n    SELECT \n        DATEADD(ww, 1, StartDate),\n        DATEADD(ww, 1, EndDate),\n        WeekNum + 1\n    FROM Weeks\n    WHERE WeekNum <= 104  -- 52 weeks/year * 2\n)\nSELECT StartDate, EndDate\nFROM Weeks\nOPTION (MAXRECURSION 0);