CREATE PROCEDURE find_missing_rows()\nBEGIN\n  SELECT \n    (SELECT end_block + 1 FROM your_table WHERE id = t.id - 1) AS expected_start_block,\n    (SELECT start_block - 1 FROM your_table WHERE id = t.id) AS expected_end_block\n  FROM \n    your_table t\n  WHERE \n    (SELECT end_block FROM your_table WHERE id = t.id - 1) + 1 != t.start_block;\nEND;