-- Create a sample table with 1 million rows\nCREATE TABLE #data (id INT PRIMARY KEY, value INT);\nINSERT INTO #data (id, value) SELECT TOP (1000000) ROW_NUMBER() OVER (ORDER BY t1.object_id), 1 FROM master.sys.all_columns t1 CROSS JOIN master.sys.all_columns t2;\n\n-- Cursor example (fast forward, read-only)\nDECLARE cur CURSOR FAST_FORWARD FOR SELECT id, value FROM #data;\nOPEN cur;\nFETCH NEXT FROM cur INTO @id, @value;\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    -- Process data here\n    FETCH NEXT FROM cur INTO @id, @value;\nEND\nCLOSE cur;\nDEALLOCATE cur;\n\n-- While loop example\nDECLARE @id INT, @value INT;\nSELECT TOP (1) @id = id, @value = value FROM #data;\nWHILE @@ROWCOUNT > 0\nBEGIN\n    -- Process data here\n    SELECT TOP (1) @id = id, @value = value FROM #data WHERE id > @id;\nEND