DECLARE @object_name nvarchar(50)\nDECLARE @object_type nvarchar(50)\nDECLARE @sql nvarchar(max)\nDECLARE @target_db nvarchar(50) = 'your_target_database_name'\n\nDECLARE cur CURSOR FOR\nSELECT name, type\nFROM sys.objects\nWHERE type IN ('V', 'P', 'FN')\n\nOPEN cur\n\nFETCH NEXT FROM cur INTO @object_name, @object_type\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SELECT @sql = OBJECT_DEFINITION(OBJECT_ID(@object_name))\n\n    EXEC ('USE ' + @target_db + '; ' + @sql)\n\n    FETCH NEXT FROM cur INTO @object_name, @object_type\nEND\n\nCLOSE cur\nDEALLOCATE cur