SELECT \n  f.forum_title, \n  f.forum_id, \n  p.post_subject, \n  p.post_user, \n  p.post_id, \n  p.post_date\nFROM \n  board_forums f\n  LEFT JOIN board_posts p ON f.forum_id = p.post_parentforum AND p.post_id = (\n    SELECT MAX(post_id) \n    FROM board_posts \n    WHERE post_parentforum = f.forum_id AND post_parentpost = 0\n  )\nWHERE \n  f.forum_status = 1\nGROUP BY \n  f.forum_id\nORDER BY \n  f.forum_positio