CREATE TABLE students (\n    id INT PRIMARY KEY,\n    -- other common attributes\n);\n\nCREATE TABLE athletes (\n    id INT PRIMARY KEY,\n    student_id INT UNIQUE NOT NULL,\n    -- athlete-specific attributes\n    CONSTRAINT FK_athletes_students FOREIGN KEY (student_id) REFERENCES students(id)\n);\n\nCREATE TABLE musicians (\n    id INT PRIMARY KEY,\n    student_id INT UNIQUE NOT NULL,\n    -- musician-specific attributes\n    CONSTRAINT FK_musicians_students FOREIGN KEY (student_id) REFERENCES students(id)\n);\n\n-- Add a check constraint to ensure each student is associated with exactly one subclass\nALTER TABLE students\nADD CONSTRAINT CK_students_subclass CHECK (\n    EXISTS (SELECT 1 FROM athletes WHERE athletes.student_id = students.id)\n    OR EXISTS (SELECT 1 FROM musicians WHERE musicians.student_id = students.id)\n    -- Add more OR conditions for other subclasses\n);