-- Get the list of tables, views, and indexes in the schema\nSELECT 'DROP TABLE ' || rtrim(TABSCHEMA) || '.' || rtrim(TABNAME) || ';'\n  FROM SYSCAT.TABLES\n WHERE TABSCHEMA = 'your_schema_name'\n UNION ALL\nSELECT 'DROP VIEW ' || rtrim(TABSCHEMA) || '.' || rtrim(TABNAME) || ';'\n  FROM SYSCAT.VIEWS\n WHERE TABSCHEMA = 'your_schema_name'\n UNION ALL\nSELECT 'DROP INDEX ' || rtrim(INDSCHEMA) || '.' || rtrim(INDNAME) || ';'\n  FROM SYSCAT.INDEXES\n WHERE INDSCHEMA = 'your_schema_name';\n\n-- Generate the DROP statements for procedures and functions\nSELECT 'DROP PROCEDURE ' || rtrim(PROCSCHEMA) || '.' || rtrim(PROCNAME) || ';'\n  FROM SYSCAT.PROCEDURES\n WHERE PROCSCHEMA = 'your_schema_name'\n UNION ALL\nSELECT 'DROP FUNCTION ' || rtrim(FUNSCHEMA) || '.' || rtrim(FUNNAME) || ';'\n  FROM SYSCAT.FUNCTIONS\n WHERE FUNSCHEMA = 'your_schema_name';\n\n-- Drop the schema itself\nDROP SCHEMA your_schema_name RESTRICT;