DECLARE @job_id UNIQUEIDENTIFIER;\nDECLARE @step_id INT;\nDECLARE @command NVARCHAR(MAX);\nDECLARE @new_command NVARCHAR(MAX);\nDECLARE @old_text NVARCHAR(50);\nDECLARE @new_text NVARCHAR(50);\n\nSET @old_text = 'old_email@example.com'; -- text to be replaced\nSET @new_text = ''; -- new text to replace with (empty string to remove)\n\nDECLARE job_cursor CURSOR FOR\nSELECT job_id, step_id, command\nFROM msdb.dbo.sysjobsteps\nWHERE subsystem = 'TSQL' -- only consider T-SQL job steps\n\nOPEN job_cursor;\n\nFETCH NEXT FROM job_cursor INTO @job_id, @step_id, @command;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @new_command = REPLACE(@command, @old_text, @new_text);\n\n    IF @new_command <> @command\n    BEGIN\n        -- update the job step command\n        EXEC msdb.dbo.sp_update_jobstep @job_id, @step_id, @new_command;\n        PRINT 'Updated job step: ' + CONVERT(VARCHAR(50), @job_id) + ' - ' + CONVERT(VARCHAR(10), @step_id);\n    END\n\n    FETCH NEXT FROM job_cursor INTO @job_id, @step_id, @command;\nEND\n\nCLOSE job_cursor;\nDEALLOCATE job_cursor;