SELECT \n    mt.name AS message_type_name,\n    c.name AS contract_name,\n    s.name AS service_name,\n    q.name AS queue_name,\n    r.name AS route_name\nFROM \n    sys.service_broker_message_types mt\nINNER JOIN \n    sys.service_broker_contracts c ON mt.message_type_id = c.message_type_id\nINNER JOIN \n    sys.service_broker_services s ON c.contract_id = s.contract_id\nINNER JOIN \n    sys.service_broker_queues q ON s.service_id = q.service_id\nINNER JOIN \n    sys.service_broker_routes r ON q.queue_id = r.queue_id;