CREATE TABLE [dbo].[WeatherMeasurement](\n  MeasurementID [int] Identity(1,1) NOT Null,\n  TownID [int] Not Null,\n  Temp [int] NOT Null,\n  Date [datetime] NOT Null,\n  PRIMARY KEY (TownID)  -- Note the primary key on TownID\n)\n\nCREATE TABLE [dbo].[WeatherMeasurementHistory](\n  MeasurementID [int] Identity(1,1) NOT Null,\n  TownID [int] Not Null,\n  Temp [int] Not Null,\n  Date [datetime] NOT Null,\n  PRIMARY KEY (TownID, Date)  -- Note the composite primary key\n)