-- Create a new profiler session\nCREATE SERVER AUDIT SPECIFICATION MyProfilerSession\nFOR SERVER AUDIT\n{\n    ADD (SQL_STATEMENT_COMPLETED)\n};\n\n-- Start the profiler session\nALTER SERVER AUDIT SPECIFICATION MyProfilerSession ENABLE;\n\n-- Execute the query\nexec sp_executesql N'SELECT COUNT_BIG(*) ...';\n\n-- Stop the profiler session\nALTER SERVER AUDIT SPECIFICATION MyProfilerSession DISABLE;\n\n-- View the captured execution plan and statistics\nSELECT * FROM sys.server_audit_specifications;\nSELECT * FROM sys.server_audit_events;