-- SSRS report code\nDECLARE @xml XML;\nSET @xml = (SELECT CONVERT(XML, 'path/to/your/xml/file.xml'));\n\nDECLARE @xslt XML;\nSET @xslt = (SELECT CONVERT(XML, 'path/to/your/xslt/file.xslt'));\n\nDECLARE @output XML;\nSET @output = @xml.query('\n  declare namespace w = "http://schemas.openxmlformats.org/wordprocessingml/2006/main";\n  declare namespace sql = "http://schemas.microsoft.com/sqlserver/2004/sqltypes";\n  for $t in //w:t\n  return <w:t>{replace($t, "[Name]", sql:variable("Name"))}</w:t>\n');\n\n-- Use the @output XML to generate the final report