CREATE TABLE summary_daily (\n    idPost INT,\n    post TEXT,\n    idCategory INT,\n    date DATE,\n    comment_count INT\n);\n\nINSERT INTO summary_daily\nSELECT post.idPost, post.post, postCategory.idCategory, comment.inputDate, COUNT(comment.idComment)\nFROM post\nINNER JOIN comment ON post.idPost = comment.idPost\nINNER JOIN postCategory ON postCategory.idPost = post.idPost\nGROUP BY post.idPost, post.post, postCategory.idCategory, comment.inputDate;