CREATE PROCEDURE [dbo].[GetRadioServiceCodesINGroup] \n    @RadioServiceGroup nvarchar(1000) = NULL\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    SELECT DISTINCT \n        rsc.RadioServiceCodeId,\n        rsc.RadioServiceCode,\n        rsc.RadioService,\n        rsc.RadioServiceCode + ' - ' + rsc.RadioService as RadioService\n    FROM \n        sbi_l_radioservicecodes rsc\n    INNER JOIN \n        sbi_l_radioservicecodegroups rscg \n    ON \n        rsc.radioservicecodeid = rscg.radioservicecodeid\n    WHERE \n        rscg.radioservicegroupid IN \n            (select val from dbo.fnParseArray(@RadioServiceGroup,',')) \n        OR \n        @RadioServiceGroup IS NULL  \n    ORDER BY \n        rsc.RadioServiceCode,\n        rsc.RadioServiceCodeId,\n        rsc.RadioService\nEND