SELECT \n    'CREATE MAINTENANCE PLAN [' + mp.name + ']' + CHAR(13) + \n    'WITH (SCHEDULE = [' + s.name + '])' + CHAR(13) + \n    'AS' + CHAR(13) + \n    'BEGIN' + CHAR(13) + \n    '    ' + mp.description + CHAR(13) + \n    'END'\nFROM \n    sys.maintenance_plans mp\nINNER JOIN \n    sys.schedules s ON mp.schedule_id = s.schedule_id\nWHERE \n    mp.name = 'YourMaintenancePlanName'