CREATE TABLE Stations (\n  StnCode VARCHAR(10) PRIMARY KEY,\n  StnName VARCHAR(50) NOT NULL,\n  StnCity VARCHAR(50) NOT NULL\n);\n\nCREATE TABLE Trains (\n  TrnNumber INT PRIMARY KEY,\n  TrnName VARCHAR(100) NOT NULL,\n  SourceStn VARCHAR(10) NOT NULL,\n  DestStn VARCHAR(10) NOT NULL,\n  DaysofWeek VARCHAR(20) NOT NULL,\n  FOREIGN KEY (SourceStn) REFERENCES Stations(StnCode),\n  FOREIGN KEY (DestStn) REFERENCES Stations(StnCode)\n);\n\nCREATE TABLE TrainHops (\n  TrnNumber INT NOT NULL,\n  StationCode VARCHAR(10) NOT NULL,\n  ArrTime TIME NOT NULL,\n  DepTime TIME,\n  HopIndex INT NOT NULL,\n  PRIMARY KEY (TrnNumber, StationCode),\n  FOREIGN KEY (TrnNumber) REFERENCES Trains(TrnNumber),\n  FOREIGN KEY (StationCode) REFERENCES Stations(StnCode)\n);