-- Monolithic table\nCREATE TABLE huge_table (\n  id INT,\n  name VARCHAR(255),\n  email VARCHAR(255),\n  address VARCHAR(255),\n  phone VARCHAR(255)\n);\n\n-- Split tables\nCREATE TABLE customers (\n  id INT,\n  name VARCHAR(255),\n  email VARCHAR(255)\n);\n\nCREATE TABLE addresses (\n  id INT,\n  customer_id INT,\n  address VARCHAR(255)\n);\n\nCREATE TABLE phones (\n  id INT,\n  customer_id INT,\n  phone VARCHAR(255)\n);