SELECT \n  COUNT(CASE WHEN DATE(date_field) = CURDATE() THEN 1 END) AS today,\n  COUNT(CASE WHEN DATE(date_field) = CURDATE() - INTERVAL 1 DAY THEN 1 END) AS yesterday,\n  COUNT(CASE WHEN DATE(date_field) = CURDATE() - INTERVAL 3 DAY THEN 1 END) AS three_days_ago,\n  COUNT(CASE WHEN DATE(date_field) = CURDATE() - INTERVAL 7 DAY THEN 1 END) AS one_week_ago,\n  COUNT(CASE WHEN DATE(date_field) = CURDATE() - INTERVAL 30 DAY THEN 1 END) AS one_month_ago,\n  COUNT(CASE WHEN YEAR(date_field) = YEAR(CURDATE()) THEN 1 END) AS this_year,\n  COUNT(CASE WHEN YEAR(date_field) = YEAR(CURDATE()) - 1 THEN 1 END) AS last_year\nFROM your_table;