DECLARE @tbXML TABLE (ParameterID INT, ParameterValue XML)\n\n-- populate the table\nINSERT INTO @tbXML (ParameterID, ParameterValue)\nVALUES (1, '<USER><User ID="1" Name="Billy"/></USER><USER><User ID="2" Name="Billy"/></USER><MANAGER><User ID="1" Name="Billy"/></MANAGER>'),\n       (2, '<USER><User ID="1" Name="John"/></USER><USER><User ID="2" Name="Billy"/></USER><MANAGER><User ID="1" Name="Billy"/></MANAGER>'),\n       (3, '<USER><User ID="1" Name="David"/></USER><USER><User ID="2" Name="Billy"/></USER><MANAGER><User ID="1" Name="Billy"/></MANAGER>')\n\nDECLARE @cnt INT\nSET @cnt = 1\n\nWHILE @cnt > 0\nBEGIN\n    UPDATE @tbXML\n    SET ParameterValue.modify('replace value of (//User/@Name[.="Billy"])[1] with "Peter"')\n    WHERE ParameterValue.value('(//User/@Name[.="Billy"])[1]', 'nvarchar(50)') = 'Billy'\n\n    SET @cnt = @@ROWCOUNT\nEND\n\nSELECT * FROM @tbXML