-- Identify dead connections\nSELECT sid, serial#, status, username \nFROM v$session \nWHERE status = 'INACTIVE' AND last_call_et > 60;\n\n-- Clean up dead connections\nBEGIN\n    FOR rec IN (SELECT sid, serial# \n                FROM v$session \n                WHERE status = 'INACTIVE' AND last_call_et > 60) LOOP\n        EXECUTE IMMEDIATE 'alter system kill session ''' || rec.sid || ',' || rec.serial# || '''';\n    END LOOP;\nEND;\n/