SELECT \n  Destaque.destaque, \n  Noticia.id, \n  Noticia.antetitulo, \n  Noticia.titulo, \n  Noticia.lead, \n  Noticia.legenda, \n  Noticia.publicacao, \n  Seccao.descricao, \n  Album.pasta,\n  Foto.ficheiro, \n  Foto.descricao, \n  Cronista.nome, \n  Cronista.profissao, \n  Cronista.ficheiro,\n  MAX(IF(mm_files.mimeType = 'audio', mm_files.id, NULL)) AS audio_id,\n  MAX(IF(mm_files.mimeType = 'video', mm_files.id, NULL)) AS video_id\nFROM \n  nt_highlights AS Destaque\n  LEFT JOIN nt_noticias  AS Noticia         ON Destaque.noticia_id = Noticia.id\n  LEFT JOIN mm_fotos     AS Foto            ON Noticia.foto_id = Foto.id\n  LEFT JOIN nt_temas     AS Seccao          ON Noticia.tema_id = Seccao.id\n  LEFT JOIN mm_albuns    AS Album           ON Foto.album_id = Album.id\n  LEFT JOIN nt_cronistas AS Cronista        ON Cronista.id = Noticia.cronista_id  \n  LEFT JOIN ntNoticias_mmFiles AS Rel       ON Rel.noticia_id = Noticia.id\n  LEFT JOIN mm_files     AS mm_files        ON mm_files.id = Rel.file_id\nWHERE \n  Destaque.area_id = 1\n  AND Noticia.paraPublicacao = 1 \n  AND Noticia.publicacao <= NOW()   \nGROUP BY \n  Noticia.id\nORDER BY \n  Destaque.destaque