declare @O table (\n    Month datetime,\n    ACount int NULL\n)\n\ninsert into @O values ('2009-01-01', 0)\ninsert into @O values ('2009-01-01', 1)\ninsert into @O values ('2008-01-01', 1)\ninsert into @O values ('2007-01-01', 0)\n\ndelete from @O\nwhere Month in (\n    select o1.Month\n    from @O o1\n    where o1.ACount = 0\n    and exists (\n        select 1\n        from @O o2\n        where o1.Month = o2.Month\n        and o2.ACount > 0\n    )\n)