WITH Calendar AS (\n  SELECT 1 AS Month\n  UNION ALL\n  SELECT Month + 1\n  FROM Calendar\n  WHERE Month < 12\n)\nSELECT \n  c.Category,\n  cal.Month,\n  COALESCE(ty.TotalRevenue, 0) AS RevThisYear,\n  COALESCE(ly.TotalRevenue, 0) AS RevLastYear\nFROM \n  Calendar cal\n  CROSS JOIN (SELECT DISTINCT Category FROM PVMonthlyStatusReport) c\n  LEFT JOIN PVMonthlyStatusReport ty \n    ON c.Category = ty.Category AND ty.Year = @FinancialYear AND ty.Month = cal.Month\n  LEFT JOIN PVMonthlyStatusReport ly \n    ON c.Category = ly.Category AND ly.Year = @FinancialYear - 1 AND ly.Month = cal.Month\nWHERE \n  c.Category = 'Bikes'\nORDER BY \n  cal.Month;