CREATE TABLE bug_reports (\n  id INT PRIMARY KEY AUTO_INCREMENT,\n  by VARCHAR(255),\n  title VARCHAR(255),\n  content TEXT,\n  datetime DATETIME,\n  application VARCHAR(255),\n  priority VARCHAR(255),\n  assigned VARCHAR(255),\n  status VARCHAR(255)\n);\n\nCREATE TABLE comments (\n  id INT PRIMARY KEY AUTO_INCREMENT,\n  bug_id INT,\n  user_id INT,\n  comment TEXT,\n  datetime DATETIME,\n  FOREIGN KEY (bug_id) REFERENCES bug_reports(id)\n);